import React from "react";
import {Link} from "react-router-dom";

const MealItemsList = ({displayInfo}) => {
  return(
    <div className="ui relaxed divided list">
      {displayInfo.map(({image, title, url}) => {
        const titleLength = title.length;
        console.log(titleLength);
        const fontSize = "" + (500 / titleLength) + "px";
        console.log(fontSize);

        return(
          <div className="item">
            <img className="item-icon" src={image} alt="meal menu item"/>
            <Link to={url}><p style={{textAlign: "center", fontSize: "100px"}}>{title}</p></Link>
          </div>
        );
      })}
    </div>
  );
}

export default MealItemsList;
