import React from "react";
import {Link} from "react-router-dom";
import {connect} from "react-redux";
import {fetchMealItems} from "../../actions";

import DisplayItems from "../subcomponents/DisplayItems";

class OrderPage extends React.Component {
  componentDidMount() {
    this.props.fetchMealItems();
  }

  render(){
    return(
      <div>
        <DisplayItems displayInfo={this.props.selectedItems}/>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return{selectedItems: Object.values(state.selectedItems)};
}

export default connect(mapStateToProps, {fetchMealItems})(OrderPage);
