import {combineReducers} from "redux";
import _ from "lodash";
import {
  SELECTED_ORDER_ITEM,
  SELECTED_ORDER_ITEMS,
  ITEMS_URL
} from "../actions/types";

const menuItemsReducer = (state={}, action) => {
  console.log("fire item reducer");
  if(action.type === SELECTED_ORDER_ITEM){
    console.log("- fire item reducer state");
    return {...state, items: action.payload};
  }else{
    console.log("- fire item reducer state");
    return state;
  }
}

const selectedItemReducer = (state={}, action) => {
  console.log("fire item reducer");
  if(action.type === SELECTED_ORDER_ITEM){
    console.log("- fire item reducer state");
    return {...state, items: action.payload};
  }else{
    console.log("- fire item reducer state");
    return state;
  }
}

const selectedItemsReducer = (state={}, action) => {
  console.log("fire items reducer");
  if(action.type === SELECTED_ORDER_ITEMS){
    console.log("- fire reducer items state");
    return {...state, ..._.mapKeys(action.payload, "id")};
  }else{
    console.log("- fire items reducer state");
    return state;
  }
}

