import {combineReducers} from "redux";

import mealImage from "../images/burger_fries.png";
import sideItemImage from "../images/side_item.png";
import drinkImage from "../images/drink.png";
import dessertImage from "../images/dessert.png";

const orderMealItemsReducer = () => {
  return[
    {
      id: 1,
      title: "Meals",
      image: mealImage,
      url: "/meals"
    },
    {
      id: 2,
      title: "Sides",
      image: sideItemImage,
      url: "/sides"
    },
    {
      id: 3,
      title: "Drinks",
      image: drinkImage,
      url: "/drinks"
    },
    {
      id: 4,
      title: "Desserts",
      image: dessertImage,
      url: "/desserts"
    }
  ];
}

const selectedMealItemReducer = (selectedMealItem = null, action) => {
  if(action.type === "MEAL_ITEM_SELECTED"){
    return action.payload;
  }

  return selectedMealItem;
}

const selectedMealItemsReducer = (selectedMealItems = null, action) => {
  if(action.type === "MEAL_ITEMS_SELECTED"){
    return action.payload;
  }

  return selectedMealItems;
}

export default combineReducers(
  // {
  //   orderMealItems: orderMealItemsReducer,
  //   selectedMealItem: selectedMealItemReducer
  // },
  {
    orderMealItems: orderMealItemsReducer,
    selectedMealItems: selectedMealItemsReducer
  },
);
