import meals from "../apis/meals";



const mealsItemsReducer = () => {
  return[
    {
      id: 1,
      title: "Southern Fried Chicken",
      price: "$20.00",
      image: mealImage,
      url: "/menu_item/1"
    },
    {
      id: 2,
      title: "Road-Side Burger",
      price: "$12.00",
      image: mealImage,
      url: "/menu_item/2"
    },
    {
      id: 3,
      title: "Srimp Crits",
      price: "$20.00",
      image: mealImage,
      url: "/menu_item/3"
    },
    {
      id: 4,
      title: "Mac & Cheese",
      price: "$5.00",
      image: mealImage,
      url: "/menu_item/4"
    }
  ];
}




import {
  SELECTED_ORDER_ITEM,
  SELECTED_ORDER_ITEMS
} from "../actions/types"

export const selectedOrderItem = (orderItem) => {
  return{
    type: SELECTED_ORDER_ITEM,
    payload: orderItem
  };
}

export const selectedOrderItems = (orderItems) => {
  return{
    type: SELECTED_ORDER_ITEMS,
    payload: orderItems
  };
}

export const fetchMealItem = (id) => async dispatch => {
  const response = await meals.get(`/meals/${id}`);

  dispatch({type: SELECTED_ORDER_ITEM, payload: response.data});
}

export const fetchMealItems = () => async dispatch => {
  const response = await meals.get("/meals");

  dispatch({type: SELECTED_ORDER_ITEMS, payload: response.data});
}
