//   const response = await meals.get(`/meals/${id}`);
//   dispatch(response.data);
// }

const mapStateToProps = (state) => {
  console.log("Mapping State to Props");
  //turns values inside an object into an array
  // return{selectedItems: Object.values(state.selectedItems)};
  // return{selectedItems: state.selectedItems};
  return{selectedItems: state.mealsItems};
}

// export default connect(mapStateToProps, {fetchMealItems})(MealPage);
export default connect(mapStateToProps)(MealPage);
