import React from "react";
import {Link} from "react-router-dom";

const MealItemsList = ({displayInfo}) => {
  return(
    <div className="ui divided list" style={{margin: "0"}}>
      {displayInfo.map(({image, title, content, url}) => {
        const fontSize = "" + (500 / title.length) + "px";

        return(
          <div className="item" style={{padding: "25px 20px"}}>
            <img className="item-icon" src={image} alt="meal menu item"/>
            <Link to={url}>
              <p
                style={{
                  textAlign: "center",
                  fontSize: {fontSize},
                  marginTop: "30px",
                  marginLeft: "80px"}}>
                {title}
              </p>
              <p>{content}</p>
            </Link>
          </div>
        );
      })}
    </div>
  );
}

export default MealItemsList;
