import meals from "../apis/meals";

import mealImage from "../images/burger_fries.png";
import sideItemImage from "../images/side_item.png";
import drinkImage from "../images/drink.png";
import dessertImage from "../images/dessert.png";

import {
  SELECTED_ORDER_ITEM,
  SELECTED_ORDER_ITEMS
} from "../actions/types"

const mealsItemsReducer = () => {
  return{
    items: [
      {
        id: 1,
        title: "Southern Fried Chicken",
        price: "$20.00",
        image: mealImage,
        url: "/menu_item/1"
      },
      {
        id: 2,
        title: "Road-Side Burger",
        price: "$12.00",
        image: mealImage,
        url: "/menu_item/2"
      },
      {
        id: 3,
        title: "Srimp Crits",
        price: "$20.00",
        image: mealImage,
        url: "/menu_item/3"
      },
      {
        id: 4,
        title: "Mac & Cheese",
        price: "$5.00",
        image: mealImage,
        url: "/menu_item/4"
      }
   ]
 }
}

export const selectedOrderItem = (orderItem) => {
  return{
    type: SELECTED_ORDER_ITEM,
    payload: orderItem
  };
}

export const selectedOrderItems = (orderItems) => {
  return{
    type: SELECTED_ORDER_ITEMS,
    payload: orderItems
  };
}

export const fetchMealItem = (id) => async dispatch => {
  const response = await meals.get(`/meals/${id}`);

  // dispatch({type: SELECTED_ORDER_ITEM, payload: response.data});
  dispatch({type: SELECTED_ORDER_ITEM, payload: mealsItemsReducer});
}

export const fetchMealItems = () => async dispatch => {
  console.log("Calling Fetch Items");
  const response = await meals.get("/meals");

  // dispatch({type: SELECTED_ORDER_ITEMS, payload: response.data});
  dispatch({type: SELECTED_ORDER_ITEMS, payload: mealsItemsReducer});
  console.log("Fetched Items Called");
}
