import {combineReducers} from "redux";

import mealImage from "../images/burger_fries.png";
import sideItemImage from "../images/side_item.png";
import drinkImage from "../images/drink.png";
import dessertImage from "../images/dessert.png";

const orderItemsReducer = () => {
  return[
    {
      id: 1,
      title: "Meals",
      image: mealImage,
      url: "/meals"
    },
    {
      id: 2,
      title: "Sides",
      image: sideItemImage,
      url: "/sides"
    },
    {
      id: 3,
      title: "Drinks",
      image: drinkImage,
      url: "/drinks"
    },
    {
      id: 4,
      title: "Desserts",
      image: dessertImage,
      url: "/desserts"
    }
  ];
}

const mealsItemsReducer = () => {
  return[
    {
      id: 1,
      title: "Southern Fried Chicken",
      price: "$20.00",
      image: mealImage,
      url: "/menu_item/1"
    },
    {
      id: 2,
      title: "Road-Side Burger",
      price: "$12.00",
      image: mealImage,
      url: "/menu_item/2"
    },
    {
      id: 3,
      title: "Srimp Crits",
      price: "$20.00",
      image: mealImage,
      url: "/menu_item/3"
    },
    {
      id: 4,
      title: "Mac & Cheese",
      price: "$5.00",
      image: mealImage,
      url: "/menu_item/4"
    }
  ];
}

const selectedOrderItemReducer = (orderItem = null, action) => {
  if(action.type === "SELECTED_ORDER_ITEM"){
    return action.payload;
  }

  return orderItem;
}

const selectedOrderItemsReducer = (orderItems = null, action) => {
  if(action.type === "SELECTED_ORDER_ITEMS"){
    return action.payload;
  }

  return orderItems;
}

export default combineReducers({
  orderItems: orderItemsReducer,
  mealItems: mealsItemsReducer,
  selectedOrderItem: selectedOrderItemReducer,
  selectedOrderItems: selectedOrderItemsReducer
});
