import {combineReducers} from "redux";
import _ from "lodash";
import {
  SELECTED_ORDER_ITEM,
  SELECTED_ORDER_ITEMS,
  SET_ITEMS_URL
} from "../actions/types";

const selectedItemsReducer = (state={}, action) => {
  switch(action.type) {
    case SELECTED_ORDER_ITEM:
      return {...state, [action.payload.id]: action.payload};
    case SELECTED_ORDER_ITEMS:
      return {...state, ..._.mapKeys(action.payload, "id")};
    default:
      return state;
  }
}

const itemsURLReducer = (state="", action) => {
  switch(action.type){
    case SET_ITEMS_URL:
      return {...state, itemsURL: action.payload};
    default:
      return state;
  }
}

export default combineReducers({
  itemsURL: itemsURLReducer,
  selectedItems: selectedItemsReducer
});
