import React from "react";
import {Link} from "react-router-dom";
import DisplayItems from "../subcomponents/DisplayItems";
import meals from "../../apis/meals";

import mealImage from "../../images/burger_fries.png";
import shoppingCart from "../../images/checkout.png";

class MealPage extends React.Component {
  componentDidMount() {
    console.log("calling");
    fetchMeals();
  }

  render(){
    return(
      <div>
       <DisplayItems displayInfo={displayInfo} menuItemButton={menuItemButton}/>
      </div>
    )
  }
}

const menuItemButton = () => {
  return(
    <div>
      <Link to="/menu_item">
        <button className="menu-items-button ui button">
          <img className="menu-add-cart" src={shoppingCart} alt="shopping cart"></img>
          <p>Add To Order</p>
        </button>
      </Link>
    </div>
   );
}

const fetchMeals = () => async () => {
  console.log("started");
  const response = await meals.get("/meals");
  console.log("done");
  console.log(response.data);
  // dispatch(response.data);
  // return response.data;
}

const fetchMeal = (id) => async dispatch => {
  const response = await meals.get(`/meals/${id}`);
  dispatch(response.data);
}

const mapStateToProps = (state) => {
  return{mealItems: state.mealItems};
}

export default connect(mapStateToProps)(MealPage);
