import React from "react";
import {Link} from "react-router-dom";
import {connect} from "react-redux";
// import {fetchMealItem} from "../../actions";
import Footer from "../subcomponents/Footer";

import mealImage from "../../images/burger_fries.png";

class BasicItemPage extends React.Component {

  componentDidMount(){
    // this.props.fetchMealItem(this.props.match.params.id);
    // console.log("Props: " + this.props);
    // console.log(this.props.match.params.id);
  }

  render(){
    console.log("Props: " + this.props);
    console.log(this.props.match.params.id);
    console.log(this.props.selectedItems);
    return(
        <div className="page-container menu-item-container">
          <div className="menu-item-top-container">
            <img src={mealImage} alt="Meal"></img>
          </div>
          <div className="menu-item-middle-container">
            <div className="menu-item-heading">
              <span className="menu-item-title">Southern Fried Chicken</span>
              <span className="menu-item-price">$20.00</span>
            </div>
            <div className="menu-item-content">
              <p>Please allow 20 minutes cook time. crispy boneless breast battered and fried, cheddar & potato gratin, coleslaw, buttermilk biscuit. GF +$4, Nashville hot +$2</p>
            </div>
          </div>
          <div className="menu-item-bottom-container">
            <Link to="/customize_item" className="customize-link">
              <button className="menu-item-button menu-item-button-customize">Customize</button>
            </Link>
            <button className="menu-item-button menu-item-button-add">+ ADD</button>
          </div>
          <Footer />
        </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state.selectedItems);
  return{selectedItems: state.selectedItems};
}

export default connect(mapStateToProps)(BasicItemPage);
