//   console.log("done");
//   console.log(response.data);
//   // dispatch(response.data);
//   // return response.data;
// }
//
// const fetchMeal = (id) => async dispatch => {
//   const response = await meals.get(`/meals/${id}`);
//   dispatch(response.data);
// }

const mapStateToProps = (state) => {
  //turns values inside an object into an array
  return{mealItems: Object.values(state.selectedItems)};
}

export default connect(mapStateToProps, {fetchMealItems})(MealPage);
