import meals from "../apis/meals";
import {
  SELECTED_ORDER_ITEM,
  SELECTED_ORDER_ITEMS,
  SET_ITEMS_URL
} from "../actions/types";


export const selectedOrderItem = (orderItem) => {
  return{
    type: SELECTED_ORDER_ITEM,
    payload: orderItem
  };
}

export const selectedOrderItems = (orderItems) => {
  return{
    type: SELECTED_ORDER_ITEMS,
    payload: orderItems
  };
}

export const fetchMealItem = (id) => async dispatch => {
  const response = await meals.get(`/meals/${id}`);

  dispatch({type: SELECTED_ORDER_ITEM, payload: response.data});
}

export const fetchMealItems = (items) => async dispatch => {
  const response = await meals.get("/" + items);

  dispatch({type: SELECTED_ORDER_ITEMS, payload: response.data});
}

export const setItemsURL = (url) => {
  dispatch({type: SET_ITEMS_URL, payload: url});
}
