import React from "react";
import {Link} from "react-router-dom";
import DisplayItems from "../subcomponents/DisplayItems";
import meals from "../../apis/meals";

import mealImage from "../../images/burger_fries.png";
import shoppingCart from "../../images/checkout.png";

const MealPage = () => {
  fetchMeals();
  return(
    <div>
      <DisplayItems displayInfo={displayInfo} menuItemButton={menuItemButton}/>
    </div>
  );
}

const menuItemButton = () => {
  return(
    <div>
      <Link to="/menu_item">
        <button className="menu-items-button ui button">
          <img className="menu-add-cart" src={shoppingCart} alt="shopping cart"></img>
          <p>Add To Order</p>
        </button>
      </Link>
    </div>
   );
}

const fetchMeals = () => {
  async (dispatch) => {
    const response = await meals.get("/meals");
    console.log(response.data);
    dispatch(response.data);
    // return response.data;
  };
}

const fetchMeal = () => {
  async dispatch => {
    const response = await meals.get(`/meals/${id}`);
    dispatch(response.data);
  }
}

const displayInfo = [
  {
    title: "Southern Fried Chicken",
    price: "$20.00",
    image: mealImage,
    url: "/menu_item"
  },
  {
    title: "Road-Side Burger",
    price: "$12.00",
    image: mealImage,
    url: "/menu_item"
  },
  {
    title: "Srimp Crits",
    price: "$20.00",
    image: mealImage,
    url: "/menu_item"
  },
  {
    title: "Mac & Cheese",
    price: "$5.00",
    image: mealImage,
    url: "/menu_item"
  }
];

export default MealPage;
