import React from "react";
import {Link} from "react-router-dom";
import {connect} from "react-redux";
import {fetchMealItems} from "../../actions";
import DisplayItems from "../subcomponents/DisplayItems";

import shoppingCart from "../../images/checkout.png";

class SidesPage extends React.Component {
  componentDidMount() {
    this.props.fetchMealItems("sides");
  }

  render(){
    return(
      <div>
       <DisplayItems displayInfo={this.props.selectedItems} menuItemButton={menuItemButton}/>
      </div>
    )
  }
}

const menuItemButton = () => {
  return(
    <div>
      <Link to="/menu_item">
        <button className="menu-items-button ui button">
          <img className="menu-add-cart" src={shoppingCart} alt="shopping cart"></img>
          <p>Add To Order</p>
        </button>
      </Link>
    </div>
   );
}

const mapStateToProps = (state) => {
  return{selectedItems: Object.values(state.selectedItems)};
}

export default connect(mapStateToProps, {fetchMealItems})(SidesPage);
