import React from "react";
import {Link} from "react-router-dom";

const MealItemsList = ({displayInfo}) => {
  return(
    <div className="ui divided list" style={{margin: "0"}}>
      {displayInfo.map(({image, title, price, url}) => {
        const fontSize = "" + (500 / title.length) + "px";

        return(
          <div className="item"
            style={{
              padding: "25px 0",
              display: "flex"
            }}>
            <div className="item-icon">
              <img src={image} alt="meal menu item"/>
            </div>
            <div>
              <Link to={url}>
                <div
                  style={{
                    // textAlign: "center",
                    fontSize: {fontSize},
                    marginLeft: "70px"}}>
                  <p style={{margin: "0"}}>{title}</p>
                  <p style={{fontSize: "40px"}}>{price}</p>
                </div>
              </Link>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default MealItemsList;
