import React from "react";
import {Link} from "react-router-dom";

const MealItemsList = ({displayInfo, isButton}) => {
  return(
    <div className="ui divided list" style={{margin: "0"}}>
      {displayInfo.map(({image, title, price, url}) => {
        const fontSize = "" + (500 / title.length) + "px";

        return(
          <div className="item">
            <div className="menu_img_wrapper">
              <img className="item-icon" src={image} alt="meal menu item"/>
            </div>
            <Link to={url} className="menu-link">
              <div style={{fontSize: {fontSize}, marginLeft: "70px"}}>
                <p style={{margin: "0"}}>{title}</p>
                <p className="menu-price-text">{price}</p>
              </div>
            </Link>
            {true
              ? console.log("yea")
              : null}
          </div>
        );
      })}
    </div>
  );
}

export default MealItemsList;
